<template>
  <div class="flex flex-col items-center justify-center">
    <div class="flex items-start gap-80">
      <router-link
        to="/"
        class="flex items-center font-extrabold text-xl relative left-40"
        ><img
          class="w-7 h-7"
          src="data:image/png;base64,"
        />Return</router-link
      >
      <div class="flex flex-col gap-5 w-1/2">
        <div v-for="comment in comments" :key="comment.id">
          <div class="text-xl">
            <span class="font-bold flex items-center gap-2"
              ><img
                class="w-14 h-14 rounded-full"
                src="data:image/jpeg;base64,"
              />{{ comment.name }}</span
            >
            <p class="">{{ comment.body }}</p>
          </div>
        </div>
      </div>
    </div>
  </div>
</template>

<script>
import axios from "axios";
export default {
  name: "Comments_",
  data() {
    return {
      postid: this.$route.params.id,
      comments: {},
    };
  },
  async mounted() {
    const url = `https://jsonplaceholder.typicode.com/comments?postId=${this.postid}`;
    try {
      this.comments = await axios.get(url).then((res) => res.data);
      console.log("====================================");
      console.log(this.comments);
      console.log("====================================");
    } catch (error) {
      this.comments = null;
    }
  },
};
</script>

